import React, { useState } from 'react';

const AuthContext = React.createContext({
    isLoggedIn: false,
    onLogout: () => {},
    onLogin: (email, password) => {}
});

export const AuthContextProvider = (props) => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect( () => {
        const storedIsuserLoggedIn = localStorage.getItem('isLoggedIn');
        if (storedIsuserLoggedIn === '1') {
          setIsLoggedIn(true);  
        }
      }, []);

    const logoutHandler = () => {
        localStorage.setItem('isLoggedIn', '0');
        setIsLoggedIn(false);
    }

    const loginHandler = () => {
        localStorage.setItem('isLoggedIn', '1');
        setIsLoggedIn(true);
    }

    return <AuthContext value= {{isLoggedIn: isLoggedIn, onLogout: loginHandler, onLogin: loginHandler}}>(props.children)</AuthContext>
}

export default AuthContext;
